mapper.select:
/* 회원 목록 조회 */ SELECT u.user_id ,u.user_pw ,u.user_name ,u.user_nickname ,ul.user_level_name ,u.user_email ,u.user_phone_number ,u.user_image ,u.total_exp ,CONCAT_WS(' ', si.region_sido_name, sgg.region_sgg_name, u.user_addr_desc) AS region ,u.total_mileage ,u.user_create_date FROM tb_user AS u INNER JOIN tb_user_level AS ul ON u.user_level_code = ul.user_level_code INNER join tb_region_sgg AS sgg ON u.region_sgg_code = sgg.region_sgg_code INNER join tb_region_sido AS si ON sgg.region_sido_code = si.region_sido_code
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
LIMIT #{startRowNum}, #{rowPerPage};

  		/* 회원등급조회 */
		SELECT
			 ul.user_level_code AS userLevelCode
			,ul.user_level_name AS userLevelName
		FROM
			tb_user_level AS ul;
