mapper.select:
/* 상품 목록 리스트 조회 */ SELECT g.goods_code , g.goods_name , g.user_id , g.goods_rental_availability , g.goods_value_price , g.goods_day_price , g.goods_reg_date , g.goods_detail , g.goods_category_code , gc.goods_category_name , CONCAT(rsido.region_sido_name,' ',rsgg.region_sgg_name) as user_region , u.user_nickname ,f.file_path FROM tb_goods AS g join tb_region_sgg AS rsgg ON rsgg.region_sgg_code = g.region_sgg_code join tb_region_sido AS rsido on rsgg.region_sido_code = rsido.region_sido_code join tb_goods_category AS gc on gc.goods_category_code = g.goods_category_code JOIN tb_user AS u ON u.user_id = g.user_id JOIN tb_file_group AS fg ON g.file_group_code = fg.file_group_code JOIN tb_file AS f ON f.file_code = fg.file_code
g.goods_state = 'activate'
and gc.goods_category_code = #{goodsCategoryCode}
and ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
and goods_rental_availability = 'Y'
and goods_rental_availability = 'N'
order by g.goods_reg_date desc
LIMIT #{startIndex}, #{pageSize}

/* 상품 목록 리스트 조회 */ SELECT g.goods_code , g.goods_name , g.user_id , g.goods_rental_availability , g.goods_value_price , g.goods_day_price , g.goods_reg_date , g.goods_detail , g.goods_category_code , gc.goods_category_name , CONCAT(rsido.region_sido_name,' ',rsgg.region_sgg_name) as user_region , u.user_nickname ,f.file_path FROM tb_goods AS g join tb_region_sgg AS rsgg ON rsgg.region_sgg_code = g.region_sgg_code join tb_region_sido AS rsido on rsgg.region_sido_code = rsido.region_sido_code join tb_goods_category AS gc on gc.goods_category_code = g.goods_category_code JOIN tb_user AS u ON u.user_id = g.user_id JOIN tb_file_group AS fg ON g.file_group_code = fg.file_group_code JOIN tb_file AS f ON f.file_code = fg.file_code
g.goods_state = 'activate' and g.user_id NOT IN ( SELECT b.blocked_user_id FROM tb_block AS b WHERE b.blocking_user_id = #{loginId})
and g.goods_category_code = #{goodsCategoryCode}
and ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
and goods_rental_availability = 'Y'
and goods_rental_availability = 'N'
order by g.goods_reg_date desc
LIMIT #{startIndex}, #{pageSize}

mapper.update:
    
		UPDATE
			tb_goods
		SET
			goods_name=#{goodsName} 
			,goods_category_code=#{goodsCategoryCode}
			,goods_value_price=#{goodsValuePrice}
			,goods_purchase_date=#{goodsPurchaseDate}
			,goods_day_price=#{goodsDayPrice}
			,goods_detail=#{goodsDetail}
		WHERE
			goods_code=#{goodsCode};
