mapper.update:
UPDATE 
	tb_user AS u
SET
	 u.user_pw = #{userPw}
	,u.user_name = #{userName}
	,u.user_email = #{userEmail}
	,u.user_phone_number = #{userPhoneNumber}
	,u.region_sgg_code = #{regionSggCode}
	,u.user_addr_desc = #{userAddrDesc}
WHERE 
	u.user_id = #{userId};

mapper.select:
SELECT
	COUNT(u.user_id)
FROM
	tb_user AS u
WHERE	
	u.user_name = #{userName}
	AND
	u.user_email = #{userEmail}
	AND
	u.user_phone_number = #{userPhoneNumber};
